import Cac from './Cac';
import Command from './Command';
export interface IOption {
    displayCommands: boolean;
}
export default class Help {
    root: Cac;
    command: Command | null;
    opts: IOption;
    constructor(root: Cac, command: Command | null, opts: IOption);
    getHelp(): string;
    output(): this;
}
