export interface IOptionsInput {
    desc: string;
    alias?: string | string[];
    default?: any;
    type?: string;
    choices?: any[];
    required?: boolean;
    [k: string]: any;
}
export interface IOptions extends IOptionsInput {
    name: string;
    names: string[];
}
export default class Options {
    options: IOptions[];
    constructor();
    add(name: string, opt: IOptionsInput | string): this;
    getDefaultsMapping(): {};
    getOptionsByType(type: string): IOptions[];
    getOptionNamesByType(type: string): string[];
    getAliasMap(): {};
    isEmpty(): boolean;
    toString(): string;
}
